    // back to top

    $("#gotop").click(function() {
        var scrollTop = $(window).scrollTop();
        // console.log(scrollTop);
        if (scrollTop <= 740) {
            $("html").animate({
                scrollTop: 0
            }, 800);
        } else {
            $("html").animate({
                scrollTop: 0
            }, 600);
        }

        return false;
    });

